mapper.update:
update actor_comment
id=#{id}, actorId=#{actorId}, type=#{type}, creater=#{creater}, createTime=#{createTime}, announceId=#{announceId}, content=#{content}, score=#{score}, status=#{status},
where id=#{id}

mapper.select:
select avg(score) 
from actor_comment

	status=0 

		and actorId=#{actorId}


		and type=#{type}

   select 

   from actor_comment


				and id=#{id} 


				and actorId=#{actorId} 


				and type=#{type} 


				and creater=#{creater} 


				and createTime=#{createTime} 


				and announceId=#{announceId} 


				and content=#{content} 


				and score=#{score} 


				and status=#{status}

select ac.id, ac.actorId, ac.type, ac.creater, ac.createTime, ac.announceId, ac.content, ac.score, ac.status, ai.name as createrName, an.title as announceTitle, an.type as announceType, spi.urlPath as createrHeadUrl, group_concat(pt.id) as announcePublicType, group_concat(pt.name) as announcePublicTypeName
from actor_comment ac join actor_info ai on ac.creater = ai.id join announce_info an on ac.announceId=an.id left join system_picture_info spi on ai.imgUuid=spi.uuid left join card_announce_public_type capt on an.id = capt.cardAnnounceId left join public_type pt on capt.publicTypeId = pt.id
ac.status=0
and ac.actorId=#{actorId}
and ac.type=#{type}
group by ac.id
order by ${sort} ${order}

select


	from actor_comment



			and id=#{id} 


			and actorId=#{actorId} 


			and type=#{type} 


