mapper.update:
update actor_info
id=#{id}, name=#{name}, realName=#{realName}, mobile=#{mobile}, password=#{password}, mkey=#{mkey}, imgUuid=#{imgUuid}, sex=#{sex}, birthDay=#{birthDay}, city=#{city}, actCities=#{actCities}, height=#{height}, weight=#{weight}, shoesSize=#{shoesSize}, size=#{size}, easemobStatusCode=#{easemobStatusCode}, lastLoginTime=#{lastLoginTime}, createTime=#{createTime}, status=#{status}, pushStatus=#{pushStatus}, logintoken=#{logintoken}, registerType=#{registerType}, authenticateLevel=#{authenticateLevel}, level=#{level}, idcard=#{idcard}, totalScore=#{totalScore}, commentNum=#{commentNum}, lon=#{lon}, lat=#{lat}, introduction=#{introduction},
where id=#{id}

mapper.select:
   select 

   from actor_info


				and id=#{id} 


				and name like CONCAT('%','${name}','%')


				and realName like CONCAT('%','${realName}','%')


				and mobile=#{mobile} 


				and password=#{password} 


				and mkey=#{mkey} 


				and imgUuid=#{imgUuid} 


				and sex=#{sex} 


				and birthDay=#{birthDay} 


				and city like CONCAT('%','${city}','%')


				and actCities=#{actCities} 


				and height=#{height} 


				and weight=#{weight} 


				and shoesSize=#{shoesSize} 


				and size=#{size} 


				and easemobStatusCode=#{easemobStatusCode} 


				and lastLoginTime=#{lastLoginTime} 


				and createTime=#{createTime} 


				and status=#{status} 


				and pushStatus=#{pushStatus} 


				and logintoken=#{logintoken} 


				and registerType=#{registerType} 


				and authenticateLevel=#{authenticateLevel} 


				and level=#{level} 


				and idcard=#{idcard} 


				and totalScore=#{totalScore} 


				and commentNum=#{commentNum} 


				and lon=#{lon} 


				and lat=#{lat}

select a.id,a.name,a.mobile,a.sex,a.createTime,a.status,a.registerType,a.level
from actor_info a


			and a.mobile like CONCAT('%','${mobile}','%')


			and a.name like CONCAT('%',#{name},'%')


			and a.realName like CONCAT('%','${realName}','%')


			and a.level=#{level}


			and a.sex=#{sex} 


			and a.status=#{status} 


			and a.registerType=#{registerType} 


			and a.id in 

	  			'${item}'




		order by ${sort} ${order}

