mapper.update:
update card_info
id=#{id}, type=#{type}, cardName=#{cardName}, sex=#{sex}, cardRoleId=#{cardRoleId}, detailRole=#{detailRole}, publicType=#{publicType}, birthDate=#{birthDate}, city=#{city}, actCities=#{actCities}, price=#{price}, height=#{height}, weight=#{weight}, shoesSize=#{shoesSize}, size=#{size}, institution=#{institution}, details=#{details}, imgUuid=#{imgUuid}, mutiImgUuid=#{mutiImgUuid}, creater=#{creater}, createTime=#{createTime}, status=#{status},
where id=#{id}

mapper.select:
select distinct(actCities) from card_info

	actCities != '全国'

		and type=#{type}

select ccc.*, cccspi.urlPath as createrHeadUrl, avg(ac.score) as avgScore from (select sc.*, group_concat(spi.urlPath) as cardImgUrls from (select c.id, c.type, c.cardName, c.sex, c.cardRoleId, c.detailRole, c.birthDate, c.city, c.actCities, c.price, c.height, c.weight, c.shoesSize, c.size, c.institution, c.details, c.creater, c.createTime, c.status, r.name as cardRoleName, ai.authenticateLevel as authenticateLevel, ai.name as createrName, ai.imgUuid as createrImgUuid, group_concat(pt.id) as publicType, group_concat(pt.name) as publicTypeNames
from card_info c left join actor_info ai on ai.id=c.creater left join role_info r on c.cardRoleId = r.id left join card_announce_public_type capt on c.id = capt.cardAnnounceId left join public_type pt on capt.publicTypeId = pt.id
and c.id=#{id}
and c.type=#{type}
and c.status=#{status}
and c.city=#{city}
and (c.actCities like concat('%', #{actCities}, '%') or c.actCities='全国')
and c.sex=#{sex}
and c.creater=#{creater}
and ai.authenticateLevel=#{authenticateLevel}
group by c.id) sc left join card_picture cp on sc.id=cp.cardId left join system_picture_info spi on cp.imgUuid = spi.uuid
and sc.publicType like concat('%', #{publicType}, '%')
and (sc.cardName like concat('%', #{keyword}, '%') or sc.detailRole like concat('%', #{keyword}, '%') or sc.city like concat('%', #{keyword}, '%') or sc.actCities like concat('%', #{keyword}, '%') or sc.institution like concat('%', #{keyword}, '%') or sc.details like concat('%', #{keyword}, '%') or sc.cardRoleName like concat('%', #{keyword}, '%') or sc.publicTypeNames like concat('%', #{keyword}, '%') )
group by sc.id) ccc left join actor_comment ac on ccc.creater = ac.actorId left join system_picture_info cccspi on ccc.createrImgUuid = cccspi.uuid group by ccc.id
order by ${sort} ${order}

   select 

   from card_info


				and id=#{id} 


				and type=#{type} 


				and cardName=#{cardName} 


				and sex=#{sex} 


				and cardRoleId=#{cardRoleId} 


				and detailRole=#{detailRole} 


				and publicType=#{publicType} 


				and birthDate=#{birthDate} 


				and city=#{city} 


				and actCities=#{actCities} 


				and price=#{price} 


				and height=#{height} 


				and weight=#{weight} 


				and shoesSize=#{shoesSize} 


				and size=#{size} 


				and institution=#{institution} 


				and details=#{details} 


				and imgUuid=#{imgUuid} 


				and mutiImgUuid=#{mutiImgUuid} 


				and creater=#{creater} 


				and createTime=#{createTime} 


				and status=#{status}


	select
	 a.id,
     a.type,
     a.cardName,
     a.sex,
     a.cardRoleId,
     a.detailRole,
     a.publicType,
     a.birthDate,
     a.city,
     a.actCities,
     a.price,
     a.height,
     a.weight,
     a.shoesSize,
     a.size,
     a.institution,
     a.details,
     a.imgUuid,
     a.mutiImgUuid,
     a.creater,
     a.createTime,
     a.status,
     b.name createrName
		from card_info a
		left join actor_info b on b.id=a.creater



				and a.id=#{id} 


				and a.type=#{type} 


				and a.cardName like CONCAT('%','${cardName}','%') 


				and a.sex=#{sex} 


				and a.cardRoleId like CONCAT('%','${cardRoleId}','%') 


				and a.detailRole like CONCAT('%','${detailRole}','%') 


				and b.name like CONCAT('%','${createrName}','%') 


				and a.publicType=#{publicType} 


				and a.birthDate=#{birthDate} 


				and a.city like CONCAT('%','${city}','%')  


				and a.actCities=#{actCities} 


				and a.price=#{price} 


				and a.height=#{height} 


				and a.weight=#{weight} 


				and a.shoesSize=#{shoesSize} 


				and a.size=#{size} 


				and a.institution=#{institution} 


				and a.details=#{details} 


				and a.imgUuid=#{imgUuid} 


				and a.mutiImgUuid=#{mutiImgUuid} 


				and a.creater=#{creater} 


				and a.createTime=#{createTime} 


				and a.status=#{status} 



			order by ${sort} ${order}

select count(id) from card_info


